const upper=e=>e.toUpperCase(),lower=e=>e.toLowerCase(),returnValue=e=>document.querySelector(e).value;document.addEventListener("DOMContentLoaded",function(){document.querySelector("#btn").addEventListener("click",function(){const e=returnValue("#text");document.querySelector("#result").innerHTML=upper(e)}),document.querySelector("#btn2").addEventListener("click",function(){const e=returnValue("#text");document.querySelector("#result").innerHTML=lower(e)})});